String dtInputDate = ((String)globalMap.get("inputDate"));
Date dtCurr = TalendDate.addDate(TalendDate.getCurrentDate(),-1,"dd");
SimpleDateFormat df = new SimpleDateFormat("yyyyMMdd");
Date dtProcessStart30 = TalendDate.addDate(TalendDate.getCurrentDate(),-30,"dd");
Date dtProcessEnd30 = TalendDate.addDate(TalendDate.getCurrentDate(),-1,"dd");
Date dtProcessStart60 = TalendDate.addDate(TalendDate.getCurrentDate(),-60,"dd");
Date dtProcessEnd60 = TalendDate.addDate(TalendDate.getCurrentDate(),-31,"dd");
Date dtProcessStart90 = TalendDate.addDate(TalendDate.getCurrentDate(),-90,"dd");
Date dtProcessEnd90 = TalendDate.addDate(TalendDate.getCurrentDate(),-61,"dd");

if (dtInputDate != null && dtInputDate.length()==8) {
	dtProcessStart30 = TalendDate.addDate(TalendDate.parseDate("yyyyMMdd", dtInputDate),-29,"dd");
	dtProcessEnd30 = TalendDate.addDate(TalendDate.parseDate("yyyyMMdd", dtInputDate),0,"dd");
	dtProcessStart60 = TalendDate.addDate(TalendDate.parseDate("yyyyMMdd", dtInputDate),-59,"dd");
	dtProcessEnd60 = TalendDate.addDate(TalendDate.parseDate("yyyyMMdd", dtInputDate),-30,"dd");
	dtProcessStart90 = TalendDate.addDate(TalendDate.parseDate("yyyyMMdd", dtInputDate),-89,"dd");
	dtProcessEnd90 = TalendDate.addDate(TalendDate.parseDate("yyyyMMdd", dtInputDate),-60,"dd");
} else {
globalMap.put("inputDate", df.format(dtCurr));
}

globalMap.put("processStartDate30", df.format(dtProcessStart30));
globalMap.put("processEndDate30", df.format(dtProcessEnd30));
globalMap.put("processStartDate60", df.format(dtProcessStart60));
globalMap.put("processEndDate60", df.format(dtProcessEnd60));
globalMap.put("processStartDate90", df.format(dtProcessStart90));
globalMap.put("processEndDate90", df.format(dtProcessEnd90));
System.out.println("inputDate: " + ((String)globalMap.get("inputDate")));
